import React, { useState, useEffect } from 'react';
import './CustomCarousel.css';

const CustomCarousel = ({ slides }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prev) => (prev + 1) % slides.length);
  };

  useEffect(() => {
    const timer = setInterval(nextSlide, 3000); // Auto-slide every 3s
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="carousel-wrapper">
      <div className="carousel-track" style={{ transform: `translateX(-${currentIndex * 100}%)` }}>
        {slides.map((slide, index) => (
          <div className="carousel-slide" key={index} style={{ background: slide.bg }}>
            {slide.image && <img src={slide.image} alt="" className="carousel-img" />}
            <h2>{slide.title}</h2>
            <p>{slide.subtitle}</p>
          </div>
        ))}
      </div>
      <div className="carousel-dots">
        {slides.map((_, i) => (
          <span key={i} className={i === currentIndex ? 'dot active' : 'dot'} onClick={() => setCurrentIndex(i)} />
        ))}
      </div>
    </div>
  );
};

export default CustomCarousel;
