import React from "react";
import Slider from "react-slick";
import "./SliderStyle.css";

const data = [
  { title: "2245", subtitle: "Fours", bg: "linear-gradient(to right, #00c6ff, #0072ff)" },
  {
    title: "2017",
    subtitle: "Winners: Mumbai Indians",
    image: "/images/mi.png",
    bg: "linear-gradient(to right, #283c86, #45a247)",
  },
  { title: "143", subtitle: "Fifties", bg: "linear-gradient(to right, #00c6ff, #0072ff)" },
];

const NumberSlider = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 600,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    responsive: [
      { breakpoint: 1024, settings: { slidesToShow: 2 } },
      { breakpoint: 768, settings: { slidesToShow: 1 } },
    ],
  };

  return (
    <div className="slider-box">
      <Slider {...settings}>
        {data.map((item, index) => (
          <div key={index} className="card" style={{ background: item.bg }}>
            {item.image && <img src={item.image} alt="" className="card-image" />}
            <h2>{item.title}</h2>
            <h4>{item.subtitle}</h4>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default NumberSlider;
